targets.expr:
sum without (name, type) (
    zfs_dataset_referenced_bytes{instance=~"$instance", pool=~"$pool"}
)
* on (instance, pool) (
    zfs_pool_free_bytes{instance="$instance", pool="$pool", type="root"} /
    zfs_pool_size_bytes{instance="$instance", pool="$pool", type="root"}
)

avg without (device) (
  rate(node_disk_io_time_seconds_total[$interval]) 
  * on (instance, device) group_left(pool, parent)
  count without() (
    max by (instance, pool, parent, device) (
      label_replace(zfs_pool_vdev_state{instance=~"$instance", pool=~"$pool", type="disk"}, "device", "$1", "path", "/dev/(.+)")
    )
  )
)
